# Сортировка №2
def bubble_sort(list):

    n = len(list)

    for i in range(n-1):
        swapped = False

        for j in range(0, n-i-1):
            if list[j] > list[j+1]:
                list[j], list[j+1] = list[j+1], list[j]
                swapped = True

        if not swapped:
            break

    return list
# Сортировка №1
def shaker_sort(list):
    i = 0
    while i != len(list):
        if list[i] < list[i-1]:
            temp1 = list[i]
            list[i] = list[i - 1]
            list[i - 1] = temp1
        i +=1
        j = len(list) - 1
        while j >= i:
            if list[j] < list[j-1]:
                temp2 = list[j]
                list[j] = list[j - 1]
                list[j - 1] = temp2
            j -= 1
    return list
# Сортировка №10 (Комплексные чисела)
def quick_sort(list):
    result = list
    if len(result) <= 1:
        return result
    else:
        q = abs(random.choice(result))
        left_nums = []
        right_nums = []
        equal_nums = []
        for number in result:
            if abs(number) < q:
                left_nums.append(number)
            elif abs(number) > q:
                right_nums.append(number)
            else:
                equal_nums.append(number)
        return quick_sort(left_nums) + equal_nums + quick_sort(right_nums)
 #Сортировка №7   
def gnom_sort(list):
    i = 1
    while i != len(list):
        if i == 0 or (list[i]) >= (list[i-1]):
            i += 1
        else:
            temp = list[i]
            list[i] = list[i-1]
            list[i-1] = temp
            i -= 1
    return list

